import React from 'react';
import axios from 'axios';
import styles from "./styles.module.css";
import { Link } from "react-router-dom";
const Cart = () => {
    const [cartItems, setCartItems] = useState([]);
    const [message, setMessage] = useState("");

    useEffect(() => {
        fetchCartItems();
    }, []);

    const fetchCartItems = async () => {
        try {
            const token = localStorage.getItem("token");
            const url = `${process.env.REACT_APP_API_URL}/api/cart/user-cart`;
            const { data } = await axios.get(url, {
                headers: { 
                    Authorization: `Bearer ${token}`,
                    'Content-Type': 'application/json'
                },
            });
            setCartItems(data);
        } catch (error) {
            console.error("Error fetching cart items:", error);
        }
    };

    const handleLogout = () => {
        localStorage.removeItem("token");
        window.location.reload();
    };

    return (
        <div className={styles.main_container}>
            <nav className={styles.navbar}>
                <h1>Flipzon</h1>
                <div className={styles.nav_links}>
                    <Link to="/" className={styles.nav_link}>Dashboard</Link>
                    <Link to="/items" className={styles.nav_link}>Items</Link>
                    <Link to="/cart" className={styles.nav_link}>My Cart</Link>
                    <Link to="/order-history" className={styles.nav_link}>Order History</Link>
                    <Link to="/deliver-item" className={styles.nav_link}>Deliver Item</Link>
                    <Link to="/support" className={styles.nav_link}>Support</Link>
                </div>
                <button className={styles.white_btn} onClick={handleLogout}>
                    Logout
                </button>
            </nav>
            <div className={styles.cart_container}>
                <h2>My Cart</h2>
                {cartItems.length === 0 ? (
                    <p>Your cart is empty</p>
                ) : (
                    <div className={styles.cart_list}>
                        {cartItems.map((cartItem) => (
                            <div key={cartItem.itemID._id} className={styles.cart_item}>
                                <h3>{cartItem.itemID.name}</h3>
                                <p>Price: ${cartItem.itemID.price}</p>
                                <p>Description: {cartItem.itemID.description}</p>
                                <p>Category: {cartItem.itemID.category}</p>
                            </div>
                        ))}
                    </div>
                )}
            </div>
        </div>
    );
};

export default Cart;
