import React, { useState, useEffect } from 'react';
import axios from 'axios';
import styles from "./styles.module.css";
import { Link } from "react-router-dom";
const OrderHistory = () => {
    // const [orders, setOrders] = useState([]);
    // const [message, setMessage] = useState("");
    // const [reviewInputs, setReviewInputs] = useState({});
    const [activeTab, setActiveTab] = useState('pendingOrders');
    const [pendingOrders, setPendingOrders] = useState([]);
    const [boughtItems, setBoughtItems] = useState([]);
    const [soldItems, setSoldItems] = useState([]);
    useEffect(() => {
        // fetchOrders();
        fetchOrderHistory();
    }, []);

    const fetchOrderHistory = async () => {
        // try {
        //     const token = localStorage.getItem("token");
        //     if (!token) {
        //         setMessage("No token found");
        //         return;
        //     }
        //     const url = `${process.env.REACT_APP_API_URL}/api/cart/buyer-orders`;
        //     const { data } = await axios.get(url, {
        //         headers: { 
        //             Authorization: `Bearer ${token}`,
        //             'Content-Type': 'application/json'
        //         },
        //     });
        //     setOrders(data);
        // } catch (error) {
        //     console.error("Error fetching orders:", error);
        //     setMessage("Error fetching orders");
        // }
        try {
            const token = localStorage.getItem("token");
            const headers = { Authorization: `Bearer ${token}` };

            const pendingOrdersResponse = await axios.get(`${process.env.REACT_APP_API_URL}/api/orders/pending`, { headers });
            setPendingOrders(pendingOrdersResponse.data);

            const boughtItemsResponse = await axios.get(`${process.env.REACT_APP_API_URL}/api/orders/bought`, { headers });
            setBoughtItems(boughtItemsResponse.data);

            const soldItemsResponse = await axios.get(`${process.env.REACT_APP_API_URL}/api/orders/sold`, { headers });
            setSoldItems(soldItemsResponse.data);
        } catch (error) {
            console.error("Error fetching order history:", error);
        }
    };
    const handleReviewChange = (orderID, value) => {
        setReviewInputs({
            ...reviewInputs,
            [orderID]: value
        });
    };
    const handleAddReview = async (orderID, sellerID) => {
        try {
            const review = reviewInputs[orderID];
            const token = localStorage.getItem("token");
            if (!token) {
                setMessage("No token found");
                return;
            }
            const url = `${process.env.REACT_APP_API_URL}/api/users/add-review`;
            const { data: res } = await axios.post(url, { sellerID, review }, {
                headers: { 
                    Authorization: `Bearer ${token}`,
                    'Content-Type': 'application/json'
                },
            });
            setMessage(res.message);
            setReviewInputs({
                ...reviewInputs,
                [orderID]: ""
            });
        } catch (error) {
            console.error("Error adding review:", error);
            setMessage("Error adding review");
        }
    };
    const renderTabContent = () => {
        switch (activeTab) {
            case 'pendingOrders':
                return (
                    <div>
                        <h3>Pending Orders</h3>
                        {pendingOrders.length === 0 ? (
                            <p>No pending orders found</p>
                        ) : (
                            <ul>
                                {pendingOrders.map((order, index) => (
                                    <li key={index}>
                                        <p>Order ID: {order._id}</p>
                                        <p>OTP: {order.otp}</p>
                                        <p>Items: {order.items.map(item => item.name).join(', ')}</p>
                                    </li>
                                ))}
                            </ul>
                        )}
                    </div>
                );
                case 'boughtItems':
                    return (
                        <div>
                            <h3>Bought Items</h3>
                            {boughtItems.length === 0 ? (
                                <p>No bought items found</p>
                            ) : (
                                <ul>
                                    {boughtItems.map((item, index) => (
                                        <li key={index}>
                                            <p>Item Name: {item.name}</p>
                                            <p>Price: ${item.price}</p>
                                        </li>
                                    ))}
                                </ul>
                            )}
                        </div>
                    );
                    case 'soldItems':
                        return (
                            <div>
                                <h3>Sold Items</h3>
                                {soldItems.length === 0 ? (
                                    <p>No sold items found</p>
                                ) : (
                                    <ul>
                                        {soldItems.map((item, index) => (
                                            <li key={index}>
                                                <p>Item Name: {item.name}</p>
                                                <p>Price: ${item.price}</p>
                                            </li>
                                        ))}
                                    </ul>
                                )}
                            </div>
                        );
                    default:
                        return null;
                }
            };
    const handleLogout = () => {
        localStorage.removeItem("token");
        window.location.reload();
    };

    return (
        <div className={styles.main_container}>
            <nav className={styles.navbar}>
                <h1>Flipzon</h1>
                <div className={styles.nav_links}>
                    <Link to="/" className={styles.nav_link}>Dashboard</Link>
                    <Link to="/items" className={styles.nav_link}>Items</Link>
                    <Link to="/cart" className={styles.nav_link}>My Cart</Link>
                    <Link to="/order-history" className={styles.nav_link}>Order History</Link>
                    <Link to="/deliver-item" className={styles.nav_link}>Deliver Item</Link>
                    <Link to="/support" className={styles.nav_link}>Support</Link>
                </div>
                <button className={styles.white_btn} onClick={handleLogout}>
                    Logout
                </button>
            </nav>
            <div className={styles.orders_container}>
                <h2>Order History</h2>
                {message && <p>{message}</p>}
                {orders.length === 0 ? (
                    <p>No orders found</p>
                ) : (
                    <div className={styles.orders_list}>
                        {orders.map((order) => (
                            <div key={order._id} className={styles.order_item}>
                                <h3>Order ID: {order._id}</h3>
                                <p>OTP: {order.otp}</p>
                                <p>Status: {order.status}</p>
                                <div className={styles.items_list}>
                                    {order.items.map((item) => (
                                        <div key={item.itemID._id} className={styles.item}>
                                            <h4>{item.name}</h4>
                                            <p>Price: ${item.price}</p>
                                            <p>Description: {item.description}</p>
                                            <p>Category: {item.category}</p>
                                        </div>
                                    ))}
                                </div>
                                {order.status === "Completed" && (
                                    <div className={styles.review_container}>
                                        <textarea
                                            placeholder="Add a review"
                                            value={reviewInputs[order._id] || ""}
                                            onChange={(e) => handleReviewChange(order._id, e.target.value)}
                                            className={styles.textarea}
                                        />
                                        <button onClick={() => handleAddReview(order._id, order.sellerID)} className={styles.green_btn}>
                                            Add Review
                                        </button>
                                    </div>
                                )}
                            </div>
                        ))}
                    </div>
                )}
            </div>
        </div>
    );
};

export default OrderHistory;
