import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';
import styles from "./styles.module.css";

const ItemDetail = () => {
    const { id } = useParams();
    const [item, setItem] = useState(null);
    const [message, setMessage] = useState("");
    const [reviews, setReviews] = useState([]);

    useEffect(() => {
        fetchItemDetails();
    }, [id]);

    const fetchItemDetails = async () => {
        try {
            const url = `${process.env.REACT_APP_API_URL}/api/items/${id}`;
            const { data } = await axios.get(url);
            setItem(data);
            fetchSellerReviews(data.sellerID);
        } catch (error) {
            console.error("Error fetching item details:", error);
        }
    };
    const fetchSellerReviews = async (sellerID) => {
        try {
            console.log ("sellerID: ", sellerID);
            const url = `${process.env.REACT_APP_API_URL}/api/reviews?sellerID=${sellerID}`;
            const { data } = await axios.get(url);
            setReviews(data);
        } catch (error) {
            console.error("Error fetching seller reviews:", error);
        }
    };
    const handleAddToCart = async () => {
        try {
            const token = localStorage.getItem("token");
            const url = `${process.env.REACT_APP_API_URL}/api/cart/add-to-cart`;
            const { data: res } = await axios.post(url, { itemID: item._id }, {
                headers: { 
                    Authorization: `Bearer ${token}`,
                    'Content-Type': 'application/json'
                },
            });
            setMessage(res.message);
            console.log("Add to cart:", res.message);
        } catch (error) {
            console.error("Error adding to cart:", error);
        }
    };

    if (!item) {
        return <div>Loading...</div>;
    }

    return (
        <div className={styles.item_detail_container}>
            <h2>{item.name}</h2>
            <p>Price: ${item.price}</p>
            <p>Category: {item.category}</p>
            <p>Description: {item.description}</p>
            <p>Description: {item.sellerID}</p>
            <div className={styles.reviews_container}>
                <h3>Seller Reviews</h3>
                {reviews.length > 0 ? (
                    reviews.map((review, index) => (
                        <div key={index} className={styles.review}>
                            <p><strong>{review.reviewerName}</strong>: {review.comment}</p>
                            <p>Rating: {review.rating}/5</p>
                        </div>
                    ))
                ) : (
                    <p>No reviews available for this seller.</p>
                )}
            </div>
            <button onClick={handleAddToCart} className={styles.green_btn}>
                Add to Cart
            </button>
            {message && <div className={styles.message}>{message}</div>}
        </div>
    );
};

export default ItemDetail;
