import React, { useState, useEffect } from 'react';
import axios from 'axios';
import styles from "./styles.module.css";
import { Link } from "react-router-dom";

const Support = () => {
    const [messages, setMessages] = useState([]);
    const [input, setInput] = useState('');

    const handleLogout = () => {
        localStorage.removeItem("token");
        window.location.reload();
    };
    useEffect(() => {
        // Start a new session when the component mounts
        setMessages([{ sender: 'bot', text: 'Hello! How can I assist you today?' }]);
    }, []);

    const handleInputChange = (e) => {
        setInput(e.target.value);
    };

    const handleSend = async () => {
        if (input.trim() === '') return;

        const newMessage = { sender: 'user', text: input };
        setMessages([...messages, newMessage]);
        setInput('');

        try {
            const response = await axios.post(`${process.env.REACT_APP_API_URL}/api/chat`, {
                message: input,
                conversation: messages,
            });
            const botMessage = { sender: 'bot', text: response.data.reply };
            setMessages((prevMessages) => [...prevMessages, botMessage]);
        } catch (error) {
            console.error('Error sending message:', error);
        }
    };

    return (
        <div className={styles.main_container}>
            <nav className={styles.navbar}>
                <h1>Flipzon</h1>
                <div className={styles.nav_links}>
                    <Link to="/" className={styles.nav_link}>Dashboard</Link>
                    <Link to="/items" className={styles.nav_link}>Items</Link>
                    <Link to="/cart" className={styles.nav_link}>My Cart</Link>
                    <Link to="/order-history" className={styles.nav_link}>Order History</Link>
                    <Link to="/deliver-item" className={styles.nav_link}>Deliver Item</Link>
                    <Link to="/support" className={styles.nav_link}>Support</Link>
                </div>
                <button className={styles.white_btn} onClick={handleLogout}>
                    Logout
                </button>
            </nav>
            <div className={styles.chat_container}>
            <div className={styles.chat_window}>
                {messages.map((msg, index) => (
                    <div key={index} className={msg.sender === 'bot' ? styles.bot_message : styles.user_message}>
                        {msg.text}
                    </div>
                ))}
            </div>
            <div className={styles.input_container}>
                <input
                    type="text"
                    value={input}
                    onChange={handleInputChange}
                    placeholder="Type your message..."
                    className={styles.input}
                />
                <button onClick={handleSend} className={styles.send_button}>
                    Send
                </button>
            </div>
        </div>
        </div>
    );
};

export default Support;
