import React, { useState } from 'react';
import axios from 'axios';
import styles from "./styles.module.css";
import { Link } from "react-router-dom";

const Items = () => {
    const [item, setItem] = useState({
        name: "",
        description: "",
        price: "",
        quantity: ""
    });
    const [message, setMessage] = useState("");

    const handleChange = ({ currentTarget: input }) => {
        setItem({ ...item, [input.name]: input.value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const token = localStorage.getItem("token");
            const url = `${process.env.REACT_APP_API_URL}/api/items/add`;
            const { data: res } = await axios.post(url, item, {
                headers: { Authorization: `Bearer ${token}` },
            });
            setMessage(res.message);
            setItem({ name: "", description: "", price: "", quantity: "" });
        } catch (error) {
            console.error("Error adding item:", error);
            if (error.response && error.response.status >= 400 && error.response.status <= 500) {
                setMessage(error.response.data.message);
            }
        }
    };

    const handleLogout = () => {
        localStorage.removeItem("token");
        window.location.reload();
    };

    return (
        <div className={styles.main_container}>
            <nav className={styles.navbar}>
                <h1>Flipzon</h1>
                <div className={styles.nav_links}>
                    <Link to="/" className={styles.nav_link}>Dashboard</Link>
                    <Link to="/items" className={styles.nav_link}>Items</Link>
                    <Link to="/cart" className={styles.nav_link}>My Cart</Link>
                    <Link to="/order-history" className={styles.nav_link}>Order History</Link>
                    <Link to="/deliver-item" className={styles.nav_link}>Deliver Item</Link>
                    <Link to="/support" className={styles.nav_link}>Support</Link>
                </div>
                <button className={styles.white_btn} onClick={handleLogout}>
                    Logout
                </button>
            </nav>
            <div className={styles.form_container}>
                <h2>Add New Item</h2>
                <form onSubmit={handleSubmit}>
                    <input
                        type="text"
                        placeholder="Name"
                        name="name"
                        onChange={handleChange}
                        value={item.name}
                        required
                        className={styles.input}
                    />
                    <input
                        type="number"
                        placeholder="Price"
                        name="price"
                        onChange={handleChange}
                        value={item.price}
                        required
                        className={styles.input}
                    />
                    <input
                        type="text"
                        placeholder="Description"
                        name="description"
                        onChange={handleChange}
                        value={item.description}
                        required
                        className={styles.input}
                    />
                    <input
                        type="text"
                        placeholder="Category"
                        name="category"
                        onChange={handleChange}
                        value={item.category}
                        required
                        className={styles.input}
                    />
                     <input
                        type="text"
                        placeholder="Seller ID"
                        name="sellerID"
                        onChange={handleChange}
                        value={item.sellerID}
                        required
                        className={styles.input}
                    />
                    {message && <div className={styles.message}>{message}</div>}
                    <button type="submit" className={styles.green_btn}>
                        Add Item
                    </button>
                </form>
            </div>
        </div>
    );
};

export default Items;
